import React, { Component } from 'react'
import Table from './Table'

class App extends Component {
  state = {
    characters: [
      {
        name: 'Charlie',
        job: 'Janitor'
      },
      {
        name: 'Mac',
        job: 'Bouncer'
      },
      {
        name: 'Dee',
        job: 'Aspiring actress'
      },
      {
        name: 'Dennis',
        job: 'Bartender'
      },
      {
        name: 'Montier',
        job: 'CEO'
      }
    ],
  }
}

removeCharacter = index => {
  const { characters } = this.state

  this.setState({
    characters:characters.filter((character, i) => {
      return i !== index
    }),
  })
}

export default App
